<?phpuse Timber\Timber;function renderContactForm($data) {    $templates = [        'blocks/contact-form.twig'    ];    $response = Timber::compile($templates, [$data]);    return $response;}add_action('rest_api_init', function () {    register_rest_route('rvr', '/modal', array(        'methods' => 'GET',        'callback' => 'renderContactForm',        'permission_callback' => '__return_true'    ));});