<?phpadd_filter('manage_users_custom_column', 'misha_last_login_column', 10, 3);add_filter('manage_users_columns', 'misha_user_last_login_column');add_action('wp_login', 'misha_collect_login_timestamp', 20, 2);function misha_last_login_column($output, $column_id, $user_id) {    if ($column_id == 'last_login') {        $last_login = get_user_meta($user_id, 'last_login', true);        $date_format = 'j M, Y';        $output = $last_login ? date($date_format, $last_login) : '-';    }    return $output;}function misha_user_last_login_column($columns) {    $columns['last_login'] = 'Last Login'; // column ID / column Title    return $columns;}function misha_collect_login_timestamp($user_login, $user) {    update_user_meta($user->ID, 'last_login', time());}