<?phpadd_action('init', function () {    // Remove the REST API endpoint.    remove_action('rest_api_init', 'wp_oembed_register_route');    // Turn off oEmbed auto discovery.    // Don't filter oEmbed results.    remove_filter('oembed_dataparse', 'wp_filter_oembed_result', 10);    // Remove oEmbed discovery links.    remove_action('wp_head', 'wp_oembed_add_discovery_links');    // Remove oEmbed-specific JavaScript from the front-end and back-end.    remove_action('wp_head', 'wp_oembed_add_host_js');}, PHP_INT_MAX - 1);add_action('init', 'remove_dns_prefetch');function remove_dns_prefetch() {    remove_action('wp_head', 'wp_resource_hints', 2, 99);}remove_action('wp_head', 'wlwmanifest_link');add_action('wp_enqueue_scripts', 'smartwp_remove_wp_block_library_css', 100);//Disable emojis in WordPressadd_action('init', 'smartwp_disable_emojis');function smartwp_remove_wp_block_library_css() {    wp_dequeue_style('wp-block-library');    wp_dequeue_style('wp-block-library-theme');    wp_dequeue_style('wc-block-style'); // Remove WooCommerce block CSS}function smartwp_disable_emojis() {    remove_action('wp_head', 'print_emoji_detection_script', 7);    remove_action('admin_print_scripts', 'print_emoji_detection_script');    remove_action('wp_print_styles', 'print_emoji_styles');    remove_filter('the_content_feed', 'wp_staticize_emoji');    remove_action('admin_print_styles', 'print_emoji_styles');    remove_filter('comment_text_rss', 'wp_staticize_emoji');    remove_filter('wp_mail', 'wp_staticize_emoji_for_email');    add_filter('tiny_mce_plugins', 'disable_emojis_tinymce');}function disable_emojis_tinymce($plugins) {    if (is_array($plugins)) {        return array_diff($plugins, array('wpemoji'));    } else {        return array();    }}