<?php//style wp-admin-loginfunction my_login_logo() { ?>    <style type="text/css">        #login {            --primary: #26afeb;        }        #login h1 a, .login h1 a {            background-image: url('<?php echo wp_get_attachment_image_src( get_theme_mod( 'custom_logo' ) , 'full' )[0]; ?>');            height: 15vh;            width: 100%;            background-size: contain;            background-repeat: no-repeat;            padding-bottom: 30px;        }        body.login {            background: white;        }        #wp-submit {            background: var(--primary);        }        #login .message {            border-left: 4px solid  var(--primary);        }        #login button span {            color:  var(--primary);        }        #loginform input:focus, #loginform button:focus {            border-color:  var(--primary);        }    </style><?php }add_action( 'login_enqueue_scripts', 'my_login_logo' );